    setSelectedEmployee(emp);
  }

  return (
    <div>
        {employee.firstname} {employee.lastname}
        <button onClick={() => handleClearOne(employee.id)}>Remove</button>
        <button onClick={() => handleSelectedEmployee()}>Update</button>
    </div>
  )
}
